import React, { useState } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Form,
  Button,
  Alert,
} from "react-bootstrap";
import axios from "axios";

const Payment = () => {
  const [formData, setFormData] = useState({
    parentName: "",
    childName: "",
    email: "",
    sessionType: "",
    date: "",
    time: "",
  });
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // First, create payment intent
      const paymentResponse = await axios.post(
        "http://localhost:5000/api/create-payment-intent",
        {
          amount: 5000, // $50.00 in cents
          currency: "usd",
        }
      );

      // Then, book the session
      const bookingResponse = await axios.post(
        "http://localhost:5000/api/book-session",
        formData
      );

      setMessage("Session booked successfully! Payment processed.");
      setFormData({
        parentName: "",
        childName: "",
        email: "",
        sessionType: "",
        date: "",
        time: "",
      });
    } catch (error) {
      setMessage("Error booking session. Please try again.");
      console.error("Booking error:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container className="mt-5">
      <Row className="justify-content-center">
        <Col md={8}>
          <Card>
            <Card.Body>
              <Card.Title className="text-primary text-center">
                ðŸ’³ Book a Tutoring Session
              </Card.Title>
              {message && (
                <Alert
                  variant={message.includes("success") ? "success" : "danger"}
                >
                  {message}
                </Alert>
              )}
              <Form onSubmit={handleSubmit}>
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Parent Name</Form.Label>
                      <Form.Control
                        type="text"
                        name="parentName"
                        value={formData.parentName}
                        onChange={handleChange}
                        required
                      />
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Child Name</Form.Label>
                      <Form.Control
                        type="text"
                        name="childName"
                        value={formData.childName}
                        onChange={handleChange}
                        required
                      />
                    </Form.Group>
                  </Col>
                </Row>
                <Form.Group className="mb-3">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Session Type</Form.Label>
                  <Form.Select
                    name="sessionType"
                    value={formData.sessionType}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select session type</option>
                    <option value="Introduction to Coding">
                      Introduction to Coding
                    </option>
                    <option value="Python Basics">Python Basics</option>
                    <option value="Web Development">Web Development</option>
                    <option value="Game Development">Game Development</option>
                    <option value="Advanced Programming">
                      Advanced Programming
                    </option>
                  </Form.Select>
                </Form.Group>
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Date</Form.Label>
                      <Form.Control
                        type="date"
                        name="date"
                        value={formData.date}
                        onChange={handleChange}
                        required
                      />
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Time</Form.Label>
                      <Form.Control
                        type="time"
                        name="time"
                        value={formData.time}
                        onChange={handleChange}
                        required
                      />
                    </Form.Group>
                  </Col>
                </Row>
                <div className="text-center">
                  <p className="text-muted">Session fee: $50.00</p>
                  <Button
                    type="submit"
                    variant="success"
                    size="lg"
                    disabled={loading}
                  >
                    {loading ? "Processing..." : "Book Session & Pay"}
                  </Button>
                </div>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Payment;
